var color = require('./randomizers/Color.js');
var border = require('./randomizers/Border.js');
var borderRadius = require('./randomizers/BorderRadius.js');

module.exports = {
  'color': color,
  'border': border,
  'borderRadius': borderRadius,
  'flip': function (){
    var x = (Math.floor(Math.random() * 2) === 0);
      if (x){
        return 1;
      }else{
        return 0;
      }
  }
};